function error_unauthorized(req, res) {
    res.json([{
        error: {
            type: 1,
            address: req.originalPath,
            description: "unauthorized user"
        }
    }]);
}

function register_user(req, res, next) {
    const devicetype = req.body && req.body.devicetype;
    if (typeof devicetype !== 'string') {
        next();
        return;
    }

    console.log(`Requested register new user: ${devicetype}`);

    if (!buttonPressed) {
        buttonPressed = true;
        setTimeout(() => {
            buttonPressed = false;
            console.log('Button is not pressed again');
        }, 60000);
        console.log('Button pressed for 1 min. Re-try registration.');

        res.json([{
            error: {
                type: 101,
                address: '',
                description: 'link button not pressed'
            }
        }]);
        return;
    }

    let username = null;

    for (const name in users) {
        if (users[name] === devicetype) {
            username = name;
            break;
        }
    }

    if (username == null) {
        username = randomstring.generate({
            length: 32,
            charset: 'hex'
        });

        users[username] = devicetype;
        console.log(`Registered new user ${username} (${devicetype})`);
    } else {
        console.log(`Using existing user ${username} (${devicetype})`);
    }
    res.json([{
        success: {
            username
        }
    }]);
}


app.use(express.json());
app.use((req, res, next) => {
    res.set('Access-Control-Allow-Credentials', 'true');
    res.set('Access-Control-Allow-Headers', 'Content-Type');
    res.set('Access-Control-Allow-Methods', 'POST, GET, OPTIONS, PUT, DELETE, HEAD');
    res.set('Access-Control-Allow-Origin', '*');
    res.set('Access-Control-Max-Age', '3600');
    res.set('Cache-Control', 'no-store, no-cache, must-revalidate, post-check=0, pre-check=0');
    res.set('Pragma', 'no-cache');
    next();
});
app.use('/api', router);

app.listen(8080, () => {
    console.log("Server ready on http://localhost:8080/");
});
